package otrclient

import "encoding/hex"
import "github.com/coyim/otr3"

var (
	alicePrivateKey = parseIntoPrivateKey("")
	bobPrivateKey   = parseIntoPrivateKey("")
)

func parseIntoPrivateKey(hexString string) otr3.PrivateKey {
	b, _ := hex.DecodeString(hexString)
	pk := new(otr3.DSAPrivateKey)
	pk.Parse(b)
	return pk
}
